'use strict';
var _ = require('underscore');
var assertions = require('./lib/type-assertion');
var errorMaker = require('./lib/error-maker');
module.exports = assertions;
module.exports.error = function (error) {
    return {
        isReq: function () {
            return assertions.isReqError(error);
        },
        isReqFor: function (field, message) {
            return assertions.isReqErrorFor(error, field, message);
        },
        isSys: function () {
            return assertions.isSysError(error);
        },
        isSysFor: function (field, message) {
            return assertions.isSysErrorFor(error, field, message);
        }
    };
};

_.extend(module.exports.error, errorMaker);
