'use strict';
var nomen = require('./nomenclt');
module.exports = function (Child, Parent) {
    var getParentName = nomen.getParent(Parent);
    Child[getParentName] = function (childSid, option) {
        return Child.findBySid(childSid, {
            fields: [nomen.parentSid(Parent)]
        }).then(function (rec) {
            return Parent.findBySid(rec[0], option);
        });
    };
};