'use strict';
var nm = require('./parenthood-nomenclt');
var methodNomen = require('./nomenclt');

module.exports = function (Parent, Child) {
    var childSidsOfName = methodNomen.sidListFor(Child);
    Parent[childSidsOfName] = function (parentSid, option) {
        var opt = option || {};
        var keyName = nm.childOf(Child.modelName(), Parent.modelName(), parentSid);
        var redis = Child.redis;
        return redis.zsetLatest(keyName, opt);
    };
};
