'use strict';
var humanize = require('humanize-plus');

module.exports = {
    /**
     * @returns {string}
     */
    childOf: function (childName, parentName, parentId) {
        return childName + '-as-child-of:' + parentName + ':' + parentId;
    },

    abandon: function (modelName) {
        return 'abandon' + humanize.capitalize(modelName);
    },
    beKidOf: function (modelName) {
        return 'beKidOf' + humanize.capitalize(modelName);
    }
};