'use strict';
var anno = require('pd-api-test-anno')();
var nomen = require('../lib/parenthood-nomenclt');

describe('Nomenclature:', function () {
    it('should return the name of parenthood', function (done) {
        anno.tcase(function () {
            console.log(nomen.childOf('son', 'father', 'JohnDoe'));
            console.log(nomen.abandon('father'));
            console.log(nomen.beKidOf('mommy'));
        }, done, 'show-parenthood-nomenclature');
    });
});
