'use strict';

module.exports = function (multiArr, cli) {
    var multiObj = cli.multi();
    (multiArr || []).forEach(function (cmdSet) {
        var args = cmdSet.length > 1 ? Array.prototype.slice.call(cmdSet, 1) : undefined;
        var cmd = cmdSet[0];
        multiObj = multiObj[cmd](args);
    });
    return multiObj;
};