'use strict';

module.exports = function (zset, WITHSCORES) {
    var result = [];
    var item = '';
    (zset || []).forEach(function (rec) {
        if (!WITHSCORES) {
            return result.push(rec);
        }
        if (item.length > 0) {
            item += ',';
        }
        item += rec;
        if (item.indexOf(',') >= 0) {
            var newItem = item.split(',');
            result.push(newItem);
            item = '';
        }
    });
    return result;
};
