'use strict';
var _ = require('underscore');

var dataForSid = function (modelName, sid) {
    return 'model:' + modelName + ':withsid:' + sid;
};

var utility = {
    sidSetOf: function (modelName) {
        return 'pd-sids-of:' + modelName;
    },
    sidValueOf: function (modelName) {
        return 'pd-sid:' + modelName;
    },
    dataForSid: dataForSid
};


module.exports = _.extend(utility, {
    specFor: function (modelName) {
        return {
            modelName: function () {
                return modelName;
            },
            sidValue: function () {
                return utility.sidValueOf(modelName);
            },
            sidSet: function () {
                return utility.sidSetOf(modelName);
            },
            dataForSid: function (id) {
                return utility.dataForSid(modelName, id);
            }
        };
    }
});