'use strict';
var phNm = require('../../parenthood-nomenclt');
var humanize = require('humanize-plus');

module.exports = {
    abandon: function (Parent) {
        return phNm.abandon(Parent.modelName());
    },
    beKidOf: function (Parent) {
        return phNm.beKidOf(Parent.modelName());
    },
    getParent: function (Parent) {
        return 'get' + humanize.capitalize(Parent.modelName());
    },
    childOf: function (Child, Parent, parentSid) {
        return phNm.childOf(Child.modelName(), Parent.modelName(), parentSid);
    },
    parentSid: function (Parent) {
        return Parent.modelName() + 'Sid';
    }
};
