'use strict';
var nm = require('./parenthood-nomenclt');
var ptNomen = require('./nomenclt');

module.exports = function (Parent, Child) {
    var parentName = Parent.modelName();
    Parent[ptNomen.adopt(Child)] = function (parentSid, childSid) {
        return Child[nm.beKidOf(parentName)](childSid, parentSid);
    };
};
