'use strict';
var methodNomen = require('./nomenclt');

module.exports = function (Parent, Child) {
    var childrenOfName = methodNomen.childListOf(Child);
    Parent[childrenOfName] = function (parentSid, option) {
        var childSidsName = methodNomen.sidListFor(Child);
        return Parent[childSidsName].call(Parent, parentSid, option).then(function (ids) {
            return Child.findBySidList(ids, option);
        });
    };
};
